import React, {FunctionComponent, useState} from 'react';
import {useProjects} from '../hooks/projects-hook';
import ProjectCard from '../component/project-card';
import './list.css';
//import { useClient } from '../hooks/client-hook';
import SearchField from "react-search-field";
import ProjectService from '../services/project-service';
import Projects from '../models/project';

const List: FunctionComponent = () => {

    const projects = useProjects();
    //const client = useClient();
   
    /*
    const [tairm, setTerm] = useState<string>(''); // terme chercher par le user
    const [pS, SetProjects] = useState<Projects[]>([]); // sert a stocker les projects correspondant à la recherche

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>): void => {
        let term = "";
        if(e.target.value !== undefined){
               term = e.target.value;
            }
           
            setTerm(term);

            if(term.length <= 1){           // si term fait moins que 2 char, on retourne un tableau vide
                SetProjects([]);
                return;
            }
            ProjectService.searchProject(tairm).then(Projects => SetProjects(pS));
    }
    */
    return (
            <div>
              <table className="container centered ">
                    <thead>
                        <tr style={{color: "black"}}>
                            <th>Project number</th>
                            <th>Project leader</th>
                        </tr>
                     </thead>
                     <tbody className="row grid">
                    {projects.map((P) =>
                        <ProjectCard key={P.ProjectId} client={P.Client} project={P}/> 
                    )} 
                    </tbody>
                </table>
            </div>
    );


}

export default List;
