import React, {FunctionComponent} from 'react';
import CandP from '../component/client-and-project-card';
import Project from '../models/project';
import Client from '../models/client';
import './list.css';
import { useProject } from '../hooks/project-hook';
import { useClient } from '../hooks/client-hook';
import { isUndefined } from 'util';

export const bothList: FunctionComponent = () =>{
    const projects = useProject();
    const clients = useClient();

    const getProject = (idCLient: number): Project => {
        try {
            var found =  projects.find(function(element){
           
                return element.ProjectId === idCLient;
            });

            return found;  
            
        } catch (error) {
            return error;
        }
      

       
         
       
    }

    return (
        <div className="col grid">
            { 
               
                clients.map((C) => <CandP key={C.ClientId} client={C} project={
                        getProject(C.ClientId)
                }/>)
            }
            
        </div>
    )
}
