import React, {FunctionComponent} from 'react';
import CandP from '../component/client-and-project-card';
import Project from '../models/project';
import Client from '../models/client';
import './list.css';
import { useProject } from '../hooks/project-hook';
import { useClient } from '../hooks/client-hook';

export const BothList: FunctionComponent = () =>{
    const projects = useProject();
    const clients = useClient();



    const getProject = (idCLient: number): Project => {
         /*
                    1) comparer le param idClient avec tout les id des objets clients
                    2) retourne l'id de de la FK du priject de ce client 
                    3) 
                    4) 
                */

                /*
                    findIndex retourne l index d'un objet passé en param
                    find retourne l'objet dont on passe l index en param

                */

        try {
            
            //Le client correspondant à l'id passé en param
            
            var client = clients.find((element) => element.ClientId == idCLient);
                
                         
            
             
            
           


           
            // doit retourner le poject correspondant à l'id trouvé au dessus. "idProjectFromClient"
            var found =  projects.find(function(element){
               
                if(client !== undefined && element.ProjectId === client.ProjectId){
                    
                    return element;
                }
                
            });

            return found;

        } catch (error) {
            return error;
        }
      
       
    }

    return (
        <div className="col grid">
            { 
               
                clients.map((C) => <CandP key={C.ClientId} client={C} project={
                        getProject(C.ClientId)
                }/>)
            }
            
        </div>
    )
}
