import Project from "./project";

//import Project from "./project";

export default class Client{

   ClientId: number;
   Company_Name: string;

    Street: string;
    ZipCode: string;
    City: string; 
    
    Email: string;
    Language: string;
    Number: string;
    IsActive: boolean;

    Projects: Project[] = [];


    /* 
        VALIDATOR
    */

   validEmail: RegExp = /^[a-zA-Z-]+@[a-zA-Z-]+\.[a-zA-Z]{2,6}$/ ;
   noValueEmail: RegExp = /^[x]{1,}@[x]{1,}.[x]{1,}$/ ;
   noValue: string = "no value";

    constructor(
        clientId: number = 0,
        companyName: string = "no value",
        street: string = "no value",
        zipCode: string = "no value",
        City: string = "no value",
        email: string = "xxx@xxx.xxx",
        language: string = "no value",
        number: string = "no value",
        isActive: boolean = true,
       projects: Project[] = []
       
    ) {
        this.ClientId = clientId;
        if(companyName !== this.noValue)
        this.Company_Name = companyName;
        this.Street = street;
        this.ZipCode = zipCode;
        this.City = City;
        this.Email = email; 
        this.Language = language;
        this.Number = number;
        this.Projects = projects;
        this.IsActive = isActive;
    }
}
