import React, { FunctionComponent, useState, useEffect } from 'react';
import { RouteComponentProps, Link} from 'react-router-dom';
import Project from '../models/project';
import ProjectService from '../services/project-service';
import "./detail.css";
import { statut } from '../models/statut';

type Params = {id: string};

const ProjectDetail: FunctionComponent<RouteComponentProps<Params>> = ({match}) => {
    
    const [project, SetProject] = useState<Project|null>(null)

    useEffect(() => {
        ProjectService.getProject(+match.params.id)
        .then(project => SetProject(project));
        }, [match.params.id]);

        var st: string|null = (project == null)?null:statut[project.Statut];
        return (
            <table className="container responsive-table centered detail">{project? (
                <div>
                    <thead>
                        <tr style={{color: "black"}}>
                           <th>Project: {project.ProjectNumber}</th> 
                           <th>
                            <Link to={`/project-edit/${project.ProjectId}`} className="btn right grey waves-effect waves-light">
                                <i className="material-icons">edit</i>
                            </Link>
                           </th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <th>throject leader: {project.ProjectLeader}</th>
                            <th>Signature date: {project.SignatureDate}</th> 
                            <th>Statut: {st}</th> 
                        </tr> 
                    </tbody>
                    
                </div>
                
                ):(
                    <h5>Pas de projet trouv√©</h5>
                )}</table>
        );
}

export default ProjectDetail;
